use criterion::{black_box, criterion_group, criterion_main, Criterion};

fn count_spaces(string: &str) -> usize {
    let mut count = 0;
    for c in string.chars() {
        if c.is_whitespace() {
            count += 1;
        }
    }

    count
}

const TEST_STRING: &str = "                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ";

fn bench_basic(c: &mut Criterion) {
    println!("Count is {}", count_spaces(black_box(TEST_STRING)));
    c.bench_function("Base approach", |b| {
        b.iter(|| count_spaces(black_box(TEST_STRING)))
    });
}

fn simd_count(c: &mut Criterion) {
    println!("Count is {}", spaced::count_spaces(black_box(TEST_STRING)));
    c.bench_function("SIMD approach", |b| {
        b.iter(|| spaced::count_spaces(black_box(TEST_STRING)))
    });
}

fn jmp_count(c: &mut Criterion) {
    println!("Count is {}", spaced::count_spaces(black_box(TEST_STRING)));
    c.bench_function("Jump table approach", |b| {
        b.iter(|| spaced::jump_tables::count_spaces(black_box(TEST_STRING)))
    });
}

criterion_group!(benches, bench_basic, simd_count, jmp_count);
criterion_main!(benches);
